  .advantages_content-line {
    left: 0;
    position: absolute;
    top: 50px;

    img {
      height: 100%;
      -o-object-fit: cover;
      object-fit: cover;
      -o-object-position: center;
      object-position: center;
      width: 100%;
    }
  }

  .advantages_content-cards {
    display: flex;
    row-gap: 24px;

    .card {
      flex: 0 0 16.66666667%;
      align-items: center;
      display: flex;
      flex-direction: column;
      padding-left: 12px;
      padding-right: 12px;

      &:nth-child(1) .card_icon { transform: translateY(-85px); }
      &:nth-child(2) .card_icon { transform: translateY(-90px); }
      &:nth-child(3) .card_icon { transform: translateY(-40px); }
      &:nth-child(4) .card_icon { transform: translateY(-77px); }
      &:nth-child(5) .card_icon { transform: translateY(-43px); }
      &:nth-child(6) .card_icon { transform: translateY(-50px); }

      .card_icon {
        padding-top: 120px;
        position: relative;

        &::before {
          border-right: 2px dashed #059a77;
          bottom: 0;
          content: "";
          height: 100%;
          left: 50%;
          margin-left: -1px;
          position: absolute;
          transform: translateY(100%);
          width: 1px;
        }
      }

      .card_info {
        position: relative;
        height: 100%;
        display: flex;
        flex-direction: column;
        align-items: flex-start;
        gap: 10px;
        padding: 20px;
        background-color: #e0f6f1;
        border-radius: $border-big-radius;
        z-index: 9;

        .card_info-title {
          font-size: 28px;
          line-height: 150%;
          color: $color-text-dark;
        }

        .card_info-text {
          font-size: 18px;
          font-family: $font-family-bold;
          color: $color-text-dark;
        }
      }
    }
  }
}

@media (max-width: 1200px) {
  .advantages_content {
    .advantages_content-cards {
